document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();document.querySelector(this.getAttribute('href')).scrollIntoView({behavior:'smooth'});});});const dayTabs=document.querySelectorAll('.day-tab');const dayContents=document.querySelectorAll('.day-content');if(dayTabs.length>0){dayTabs.forEach(tab=>{tab.addEventListener('click',function(){const day=this.getAttribute('data-day');dayTabs.forEach(t=>t.classList.remove('active'));dayContents.forEach(c=>c.classList.remove('active'));this.classList.add('active');document.querySelector(`.day-content[data-day="${day}"]`).classList.add('active');});});}
const infoTabs=document.querySelectorAll('.info-tab');if(infoTabs.length>0){infoTabs.forEach(tab=>{tab.addEventListener('click',()=>{document.querySelectorAll('.info-tab').forEach(t=>t.classList.remove('active'));document.querySelectorAll('.tab-content').forEach(c=>c.classList.remove('active'));tab.classList.add('active');const tabId=tab.getAttribute('data-tab');document.getElementById(tabId).classList.add('active');});});}
let lastScrollTop=0;const header=document.querySelector('.main-header');window.addEventListener('scroll',function(){const scrollTop=window.pageYOffset||document.documentElement.scrollTop;if(scrollTop>lastScrollTop&&scrollTop>100){header.classList.add('scroll-down');header.classList.remove('scroll-up');}else{header.classList.remove('scroll-down');header.classList.add('scroll-up');}
lastScrollTop=scrollTop;});});;